PRAGMA defer_foreign_keys=TRUE;
CREATE TABLE dramas (
  id TEXT PRIMARY KEY,
  title TEXT NOT NULL,
  description TEXT,
  poster TEXT,
  backdrop TEXT,
  rating REAL DEFAULT 0,
  year INTEGER,
  created_at TEXT,
  updated_at TEXT
);
CREATE TABLE films (
  id TEXT PRIMARY KEY,
  title TEXT NOT NULL,
  description TEXT,
  poster TEXT,
  backdrop TEXT,
  rating REAL DEFAULT 0,
  year INTEGER,
  created_at TEXT,
  updated_at TEXT
);
CREATE TABLE animes (
  id TEXT PRIMARY KEY,
  title TEXT NOT NULL,
  description TEXT,
  poster TEXT,
  backdrop TEXT,
  rating REAL DEFAULT 0,
  year INTEGER,
  created_at TEXT,
  updated_at TEXT
);
CREATE TABLE bollywood (
  id TEXT PRIMARY KEY,
  title TEXT NOT NULL,
  description TEXT,
  poster TEXT,
  backdrop TEXT,
  rating REAL DEFAULT 0,
  year INTEGER,
  created_at TEXT,
  updated_at TEXT
);
CREATE TABLE scraping_logs (
  id TEXT PRIMARY KEY,
  source TEXT NOT NULL,
  content_type TEXT NOT NULL,
  status TEXT,
  items_scraped INTEGER DEFAULT 0,
  items_count INTEGER DEFAULT 0,
  errors INTEGER DEFAULT 0,
  errors_count INTEGER DEFAULT 0,
  duration INTEGER DEFAULT 0,
  success BOOLEAN DEFAULT 0,
  details TEXT,
  created_at TEXT,
  updated_at TEXT
);
CREATE TABLE users (
  id TEXT PRIMARY KEY,
  email TEXT UNIQUE NOT NULL,
  name TEXT,
  avatar TEXT,
  created_at TEXT,
  updated_at TEXT
);
CREATE TABLE favorites (
  id TEXT PRIMARY KEY,
  user_id TEXT NOT NULL,
  content_id TEXT NOT NULL,
  content_type TEXT NOT NULL,
  created_at TEXT,
  FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE
);
CREATE TABLE views (
  id TEXT PRIMARY KEY,
  user_id TEXT NOT NULL,
  content_id TEXT NOT NULL,
  content_type TEXT NOT NULL,
  progress INTEGER DEFAULT 0,
  created_at TEXT,
  updated_at TEXT,
  FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE
);
CREATE TABLE recommendations (
  id TEXT PRIMARY KEY,
  user_id TEXT NOT NULL,
  content_id TEXT NOT NULL,
  content_type TEXT NOT NULL,
  score REAL DEFAULT 0,
  reason TEXT,
  created_at TEXT,
  FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE
);
CREATE INDEX idx_dramas_year ON dramas(year);
CREATE INDEX idx_films_year ON films(year);
CREATE INDEX idx_animes_year ON animes(year);
CREATE INDEX idx_bollywood_year ON bollywood(year);
CREATE INDEX idx_favorites_user ON favorites(user_id);
CREATE INDEX idx_views_user ON views(user_id);
CREATE INDEX idx_views_content ON views(content_id, content_type);
CREATE INDEX idx_recommendations_user ON recommendations(user_id);