INSERT INTO animes VALUES('23011959-6812-4764-8e34-e701e74b3adc','Teogonia',replace('Des terres hostiles et envahies de monstres : c’est là que vit Kai, un jeune homme assurant la défense de son village. Risquer sa vie pour vaincre les monstres, découvrir des pierres divines dans leurs entrailles, les consommer pour gagner en puissance : voilà son quotidien.\nMais un jour lui viennent en tête d’étranges souvenirs, ceux d’un monde différent : le nôtre…','\n',char(10)),'https://v6.voiranime.com/wp-content/uploads/2025/03/thumb_67daebdb9be1a-193x278.jpg','',4.9,NULL,'2025-05-01T23:39:07.501Z','2025-05-01T21:39:07.685Z');
