INSERT INTO animes VALUES('f6fea1d9-597f-49d6-9133-21b593b2ab98','Moonrise',replace('L’histoire se déroule dans un futur proche, sur Terre et sur la Lune.\nOn y suit le quotidien de deux hommes, Jack et Al, qui doivent affronter des difficultés diverses dans le monde vaste qu’est le cosmos.','\n',char(10)),'https://v6.voiranime.com/wp-content/uploads/2025/03/thumb_67daebeac9e55-193x278.jpg','',4.9,NULL,'2025-05-01T23:52:38.766Z','2025-05-01T21:52:38.939Z');
