INSERT INTO animes VALUES('6c30452e-b03b-4976-a8db-9ed985d4e449','OVERLORD: The Sacred Kingdom',replace('Il s’agit de l’adaptation de l’arc Holy Kingdom du roman Overlord. Cet arc se déroule entre les épisodes 7 et 8 de la quatrième saison.\nJaldabaoth décide d’envahir le Saint Royaume de Roble avec les demi-humains. Conscient de la menace, le Saint Royaume prévoit de faire appel à Momon pour l’aider dans cette guerre.','\n',char(10)),'https://v6.voiranime.com/wp-content/uploads/2025/04/thumb_680bb99f94bf2-193x278.png','',4.6,0,'2025-05-01T19:50:19.924Z','2025-05-01T19:50:19.924Z');
