INSERT INTO animes VALUES('3d725539-ba1e-4ba3-bc3d-3eaf6cfe038a','Ao no Miburo','1863. Alors que le Bakufu s’affaiblit et que le Japon entre progressivement dans une nouvelle ère, Nio, jeune orphelin aux cheveux blonds, croise deux rônins qui vont changer sa vie.','https://v6.voiranime.com/wp-content/uploads/2024/09/thumb_66f03e1125621-193x278.jpg','',4.9,0,'2025-05-01T19:52:58.586Z','2025-05-01T19:52:58.586Z');
