INSERT INTO animes VALUES('852df6c7-c254-4a9d-bb0b-a2f24e1ef22c','2.5 Dimensional Seduction',replace('“Je ne suis pas intéressé par les vraies femmes ! Ma femme, Lilliel, est en 2 dimensions !”. Mais quelque chose d’inattendue apparaît devant Okumura, le président du club de manga… la vraie Lilliel ?!\nLa comédie romantique avec ce personnage de cosplay commence !','\n',char(10)),'https://v6.voiranime.com/wp-content/uploads/2024/06/thumb_6671a44f91e20-193x278.jpg','',2.5,NULL,'2025-05-01T23:42:02.596Z','2025-05-01T21:42:02.767Z');
