INSERT INTO animes VALUES('0df4edfd-459d-46b7-9518-e3a8911595fb','Trinity Seven Movie – Tenkuu Toshokan to Shinku no Maou',replace('Il s’agit du deuxième film d’animation se basant sur le manga Trinity Seven.\nKasuga Arata et Lilith Asami enquêtent dans des ruines quand tout à coup, une lumière blanche laisse apparaître un homme qui ressemble à Arata. Il s’avère être un seigneur-démon et tandis qu’il vient de retrouver tous ses pouvoirs, il n’a qu’un seul but : détruire le monde. Mais il fait également une surprenante révélation à Arata : Lilith est la fille du plus puissant des seigneurs-démons !','\n',char(10)),'https://v6.voiranime.com/wp-content/uploads/2024/01/thumb_65a2c5078e127-193x278.jpg','',4.2,NULL,'2025-05-01T23:42:38.246Z','2025-05-01T21:42:38.429Z');
