INSERT INTO animes VALUES('ae410a75-c79c-4fb4-8798-2f3975e578d5','Lazarus (JAP)',replace('2052. Alors que la planète Terre connaît une ère de paix et de prospérité sans précédent, le professeur Skinner, neuroscientifique détenteur d’un prix Nobel, met au point l’Hapuna. Ce médicament miracle est une véritable révolution puisqu’il peut tout soigner, sans effet secondaire ! Mais alors qu’il est mit sur le marché, son concepteur disparaît sans laisser de traces.\nTrois ans plus tard, le professeur Skinner réapparaît et indique que son médicament a un effet secondaire de taille : tous ceux qui l’ont utilisé mourront au bout de trois ans. Le compte à rebours est lancé pour “Lazarus”, un groupe de 5 personnes chargées de retrouver le scientifique et de trouver un remède à l’Hapuna.','\n',char(10)),'https://v6.voiranime.com/wp-content/uploads/2025/03/thumb_67daeaa6de4ef-193x278.png','',4.9,NULL,'2025-05-01T23:38:39.993Z','2025-05-01T23:38:39.993Z');
INSERT INTO animes VALUES('46364ff4-282e-4fdf-8ebf-9abd8ae8f906','Lazarus (VF)',replace('2052. Alors que la planète Terre connaît une ère de paix et de prospérité sans précédent, le professeur Skinner, neuroscientifique détenteur d’un prix Nobel, met au point l’Hapuna. Ce médicament miracle est une véritable révolution puisqu’il peut tout soigner, sans effet secondaire ! Mais alors qu’il est mit sur le marché, son concepteur disparaît sans laisser de traces.\nTrois ans plus tard, le professeur Skinner réapparaît et indique que son médicament a un effet secondaire de taille : tous ceux qui l’ont utilisé mourront au bout de trois ans. Le compte à rebours est lancé pour “Lazarus”, un groupe de 5 personnes chargées de retrouver le scientifique et de trouver un remède à l’Hapuna.','\n',char(10)),'https://v6.voiranime.com/wp-content/uploads/2025/03/thumb_67daeaa6de4ef-193x278.png','',4.9,NULL,'2025-05-01T23:38:43.931Z','2025-05-01T23:38:43.931Z');
