INSERT INTO animes VALUES('f5fbb935-fa96-40f6-aae1-03af976133b0','COBRA THE ANIMATION',replace('Cobra est le pirate le plus rechercher de toute la galaxie. Las de cette chasse à l’homme il pris la décision de modifier son apparence physique et mental. Mission réussit car pendant des années il vécu une paisible vie sans savoir qui il était réellement jusqu’au jour ou subitement tout lui revint à l’esprit y comprit son arme caché dans son bras gauche. Pour lui une nouvelle aventure débuta.\nEn sillonnant l’espace à la recherche d’une nouvelle aventure il fera la connaissance de la belle Secret qui est le portrait craché de Dominique, son ancienne amante. La jeune femme lui demandera de l’emmener sur la planète Garon avec en sa possession la clé de Shiva. De nouveau, il devra faire face a ses ennemis de toujours, les pirates de l’espace.','\n',char(10)),'https://v6.voiranime.com/wp-content/uploads/2025/01/thumb_679b70fb70770-193x278.jpg','',3.3,NULL,'2025-05-01T23:52:58.530Z','2025-05-01T23:52:58.530Z');
INSERT INTO animes VALUES('1ec92d69-c62d-4399-ab18-3ba83cfe4ef6','Maou 2099 (VF)','Année 2099 de l’ère fusionnée, Shinjuku. L’éblouissante prospérité de cette gigantesque cité-État cache une part d’ombre. C’est ici, dans cette mégapole qui représente l’apogée du développement humain, que le légendaire Seigneur Démon Veltol fait sa seconde apparition. Pour régner sur ce nouveau monde, il devra s’emparer lui-même de l’avenir !','https://v6.voiranime.com/wp-content/uploads/2024/09/thumb_66f03e0d551d6-193x278.jpg','',10,NULL,'2025-05-01T23:53:02.449Z','2025-05-01T23:53:02.449Z');
INSERT INTO animes VALUES('1ab77f0c-feb7-4594-9941-6555583a3453','Aquarion Logos',replace('Il s’agit d’une nouvelle série Aquarion pour fêter les dix ans de la franchise.\nCette histoire est centrée sur le personnage de Kaibuki Akira et de son unité. A eux tous, ils représentent le dernière espoir de l’humanité.\nCette unité est chargée de combattre des monstres appelés MJBK qui apparaissent grâce à des écritures.','\n',char(10)),'https://v6.voiranime.com/wp-content/uploads/2025/01/thumb_677fe8c1d5e62-193x278.jpg','',2.7,NULL,'2025-05-01T23:53:06.398Z','2025-05-01T23:53:06.398Z');
INSERT INTO animes VALUES('7245f21d-4d4d-41a9-a7d0-cee92bb29034','Dota: Dragon’s Blood (VF)',replace('Un chevalier dragon en conflit mais courageux doit utiliser le pouvoir du dragon en lui pour arrêter un démon mortel.\nPS : Anime basé sur le jeu en ligne.','\n',char(10)),'https://v6.voiranime.com/wp-content/uploads/2021/03/D1fV23ds6A4er5dta-193x278.jpg','',4.2,NULL,'2025-05-01T23:55:00.921Z','2025-05-01T23:55:00.921Z');
INSERT INTO animes VALUES('705926af-796f-43cf-b8fa-c814a76b7ecf','Maou 2099','Année 2099 de l’ère fusionnée, Shinjuku. L’éblouissante prospérité de cette gigantesque cité-État cache une part d’ombre. C’est ici, dans cette mégapole qui représente l’apogée du développement humain, que le légendaire Seigneur Démon Veltol fait sa seconde apparition. Pour régner sur ce nouveau monde, il devra s’emparer lui-même de l’avenir !','https://v6.voiranime.com/wp-content/uploads/2024/09/thumb_66f03e0d551d6-193x278.jpg','',10,NULL,'2025-05-01T23:53:10.347Z','2025-05-01T23:53:10.347Z');
