INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('3b172464-b325-4106-9954-dc40ed4396fd', 'Ueno-san wa Bukiyou', 'Ueno est la présidente du club de sciences de son lycée. Souvent renfrognée, elle passe son temps et dépense toute son énergie à élaborer de nouvelles inventions. Mais il y a une chose qu’elle n’est pas capable de créer de toute pièce : un remède contre son incroyable maladresse en amour ! Ueno est en effet amoureuse de son camarade, Tanaka, mais elle ne sait pas comment s’y prendre, et le concept de drague lui échappe totalement… D’ailleurs, quand ce dernier lui tend des perches, elle ne le remarque même pas !', 'https://v6.voiranime.com/wp-content/uploads/2025/04/thumb_67efe233ec5b6-193x278.jpg', '', 3.2, NULL, '2025-05-01T23:55:08.867Z', '2025-05-01T21:55:09.055Z');
INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('9f56cbe5-e13a-4829-9554-a97bffdba66e', 'Gugure! Kokkuri-san', 'L’histoire nous entraîne dans le quotidien mouvementé de la petite Kohina Ichimatsu qui se retrouve hantée par Kokkuri-san, un esprit de bas étage, complètement malchanceux. Mais on devrait plus plaindre Kokkuri-san, qui n’est pas un mauvais esprit, plutôt que Kohina, tant son inefficacité et sa malchance sont grande…', 'https://v6.voiranime.com/wp-content/uploads/2025/04/thumb_67efe23c7ede7-193x278.png', '', 4.1, NULL, '2025-05-01T23:55:12.856Z', '2025-05-01T21:55:13.038Z');
INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('219498f8-240b-4582-a6fb-44a11559ca77', 'The Brilliant Healer’s New Life in the Shadows', 'Zenos travaillait dur pour perfectionner ses compétences au profit de ses amis, et si un membre du groupe était blessé, il le soignait en un instant. Il était un guérisseur de génie. Cependant, il a été ostracisé sans cérémonie par les membres de son équipe qui n’ont pas apprécié sa contribution.
INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('8999681c-0ce3-4cea-92c1-e65c7b89d9c2', 'Compass Combat Providence Analysis System', 'Le héros rebelle 13 refuse un partenaire. jusqu’à ce que Jin le rejoigne. Pourront-ils se battre pour maintenir COMPASS2.0 en marche et apporter la paix ?', 'https://v6.voiranime.com/wp-content/uploads/2025/03/thumb_67daebdfb2400-193x278.jpg', '', 4.8, 0, '2025-05-01T19:48:37.830Z', '2025-05-01T19:48:37.830Z');
