INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('1b1eecb0-36e0-4e37-a044-becd2a6ce977', 'The Red Ranger Becomes an Adventurer in Another World', 'Tougo Asagaki, un Sentai Hero, a été vaincu par l’ennemi lors de la bataille finale contre l’organisation maléfique locale. Il semble avoir perdu la vie lors de l’affrontement, mais lorsqu’il revient à lui, il s’est réincarné dans un monde remplis de monstres et de magie ! Dans cet autre monde, il devient un aventurier pour sauver les gens dans le besoin. Pour cela il peut toujours se transformer en Kizuna Red et continuer à se battre pour forger des liens et faire régner la justice !', 'https://v6.voiranime.com/wp-content/uploads/2024/12/thumb_6762e79059e5e-193x278.jpg', '', 4.8, NULL, '2025-05-01T23:39:51.491Z', '2025-05-01T21:39:51.810Z');
