INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('05585ba9-f3c5-4c09-b158-c9a6d0abae84', 'Sounan desu ka?', 'À la suite d’un crash de leur avion, un groupe de lycéennes se retrouve échoué sur une île déserte. Leur unique but désormais est de survivre, et pour cela, il faut se résigner à faire des choses qu’elles n’auraient jamais imaginé faire en temps normal : manger des insectes vivants, construire des pièges, apprendre à s’orienter en forêt…', 'https://v6.voiranime.com/wp-content/uploads/2025/04/thumb_67f1167ac6a24-193x278.jpg', '', 3.8, NULL, '2025-05-01T23:41:42.706Z', '2025-05-01T21:41:42.879Z');
INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('edbb93ed-9039-49d9-b3fb-f2eb19ebaa33', 'Magic Maker: How to Make Magic in Another World (VF)', 'Un homme rêvant d’utiliser la magie est réincarné sous les traits d’un jeune garçon prénommé Shion. Ses attentes sont immenses lorsqu’il comprend qu’il a atterri dans un autre monde, mais le désespoir ne tarde pas à le rattraper. La magie n’existe pas non plus dans celui-ci. Sa sœur décide alors de l’emmener au lac pour lui montrer un étrange phénomène…', 'https://v6.voiranime.com/wp-content/uploads/2024/12/thumb_6762e71da2628-193x278.jpg', '', 4.9, 0, '2025-05-01T19:50:57.774Z', '2025-05-01T19:50:57.774Z');
