INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('dad04588-a7a7-405b-917b-90305cd40f37', 'Witch Watch', 'Morihito Otogi possède une force surhumaine car il est le descendant d’une lignée d’ogres. Un jour, son père lui annonce qu’il doit devenir le familier de Nico, son amie d’enfance qui est une sorcière. Ils vont vivre ensemble et il devra la protéger de tout danger. Nico, amoureuse de Morihito, est ravie de cette cohabitation, bien que son amour ne soit pas réciproque. Cependant, Morihito prend très au sérieux son rôle de protecteur car une menace imminente pèse sur elle.', 'https://v6.voiranime.com/wp-content/uploads/2025/03/thumb_67daeab6b19c6-193x278.jpg', '', 4.9, NULL, '2025-05-01T23:50:54.498Z', '2025-05-01T21:50:55.242Z');
