INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('6366a205-54d8-4bcb-8997-b45ddff6ec69', 'Rock is a lady’s modesty', 'Issue de la classe moyenne, une jeune fille est bien obligée de se frotter au monde de la bourgeoisie lorsque sa mère se remarie avec un homme fortuné. Transférée dans une école privée destinée à former l’élite, elle fait la rencontre d’Otawa, la fille d’un célèbre homme politique, avec qui elle va monter… un groupe de rock !', 'https://v6.voiranime.com/wp-content/uploads/2025/03/thumb_67daea9ebcf75-193x278.jpg', '', 4.9, 0, '2025-05-01T19:52:05.839Z', '2025-05-01T19:52:05.839Z');
INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('5e123e3a-ceda-48ee-93e2-d8c12ba008d4', 'Mobile Suit Gundam GQuuuuuuX', 'Amate Yuzuriha, une lycéenne vivant paisiblement dans une colonie spatiale, est impliquée dans des duels illégaux de mobile suits appelés “Clan Battle”. Amate y pilote GQuuuuuuX et se fait appeler Machu. Lors de ces compétitions, elle rencontre une réfugiée de guerre, Nyaan.
INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('75e45d46-34dd-4080-88d1-bee0ea2bf6aa', 'Once Upon a Witch’s Death', 'Alors qu’elle vient tout juste de fêter ses 17 ans, Meg Raspberry apprend qu’elle est victime d’une malédiction et qu’il ne lui reste plus qu’un an à vivre. Désespérée, elle consulte Faust, une sorcière chevronnée qui est aussi sa mentor. Cette dernière lui révèle alors qu’elle peut lever la malédiction en collectant des larmes de joie, afin d’obtenir une “graine de vie”. La jeune apprentie-sorcière débute ainsi une aventure pleine d’espoir et de rencontres.', 'https://v6.voiranime.com/wp-content/uploads/2025/03/thumb_67daea92725e3-193x278.jpg', '', 4.9, 0, '2025-05-01T19:52:13.332Z', '2025-05-01T19:52:13.332Z');
