INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('a2addfc8-e1c1-4d85-b386-b1e0089c2193', 'Soukyuu no Fafner: BEHIND THE LINE', 'Il s’agit d’un spin-off autonome se déroulant dans l’univers de Soukyuu no Fafner. L’épisode présentera une vision de l’univers loin des combats.', 'https://v6.voiranime.com/wp-content/uploads/2025/02/thumb_67ab95ca87dfb-193x278.jpg', '', 3.2, NULL, '2025-05-01T23:44:02.561Z', '2025-05-01T23:44:02.561Z');
