INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('fd335a13-422a-4472-8ba0-0bd59b41280f', 'Danmachi 5', 'Saison 5 de “Danmachi”', 'https://v6.voiranime.com/wp-content/uploads/2024/09/thumb_66f03d75b3d4a-193x278.jpg', '', 5, NULL, '2025-05-01T23:52:06.812Z', '2025-05-01T23:52:06.812Z');
INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('377338d8-bac6-4539-979f-d183a0832f16', 'I’ll Become a Villainess That Will Go Down in History (VF)', 'J’ai toujours détesté les gentilles héroïnes qui essayent toujours de ne blesser personne. Je ne veux pas vivre dans un monde plein de faux-semblants ! Alors le jour où je finis soudainement par mourir prématurément renversée par un camion, je suis ravie de constater que j’ai été réincarnée dans un autre monde dans la peau d’Alicia, la méchante d’un otome game auquel je jouais de mon vivant.
INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('f35b7304-81a0-40e7-8bec-f9df97390e3f', 'How I Attended an All-Guy’s Mixer', 'Tokiwa, étudiant à l’université, reçoit un jour l’invitation de son amie Suo à un blind date. Lorsqu’il arrive sur place, dans un pub plutôt chic, il se rend compte que les “prétendants” sont tous des hommes…', 'https://v6.voiranime.com/wp-content/uploads/2025/01/thumb_677fe8bdc8ce4-193x278.jpg', '', 4, NULL, '2025-05-01T23:52:14.778Z', '2025-05-01T23:52:14.778Z');
INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('34585aa8-14d7-4be4-b634-04ae04b63311', 'Psychic School Wars', 'Ryoichi Kyogoku, un jeune garçon qui vient de rejoindre le lycée de Kamakura, possède un pouvoir exceptionnel. En effet, ce garçon est un télépathe et est capable de contrôler les gens. Il va utiliser ce pouvoir pour accomplir son but, prendre le contrôle de l’école.
