INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('51bc9991-adbc-40f9-9814-d6234cbd5cd4', 'Lastman 2 (VF)', 'Saison 2 de Lastman', 'https://v6.voiranime.com/wp-content/uploads/2024/08/lastman-s2-va-193x278.jpg', '', 2, NULL, '2025-05-01T23:54:01.807Z', '2025-05-01T23:54:01.807Z');
INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('c2dd905f-c6ac-4c11-a857-7bc722796d06', 'GREAT PRETENDER: razbliuto', 'Cette suite de l’anime Great Pretender se centre sur Dorothy, une arnaqueuse exceptionnelle qui a été partenaire et amante de Laurent Thierry. Elle est retrouvée dans un petit village en bord de mer, à proximité de la ville taïwanaise de Taipei, après avoir survécu miraculeusement au tir de Liu Xiao. Pourchassée par la société noire de Taipei, elle part pour Kyoto où quelque chose d’impensable se prépare.', 'https://v6.voiranime.com/wp-content/uploads/2024/02/thumb_65d864babf062-193x278.jpg', '', 4.4, NULL, '2025-05-01T23:54:05.763Z', '2025-05-01T23:54:05.763Z');
INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('9a90fb65-81cf-47b7-be14-02154814a45c', 'Dead Mount Death Play', 'Dans un monde rempli de magie vit un nécromancien hors pair, réputé comme le meilleur, surnommé le Dieu des Cadavres. À travers un labyrinthe, il crée d’innombrables monstres provoquant des calamités.
