INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('d073b91b-d851-4eff-bea3-5b0898e5d94e', 'Skull-face Bookseller Honda-san', 'L’histoire suit le quotidien de Honda, un squelette travaillant dans une librairie japonaise. Toujours avec humour, Honda et ses collègues nous font découvrir plus en profondeur le métier de libraire, avec son lot d’avantages, d’inconvénients et les différents types de clients qu’ils ont croisés !', 'https://v6.voiranime.com/wp-content/uploads/2024/12/thumb_676156b33f45a-193x278.jpg', '', 4, NULL, '2025-05-01T23:55:36.697Z', '2025-05-01T23:55:36.697Z');
INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('3752300e-8d13-4f4f-bceb-eeac454e1dee', 'Kenzen Robo Daimidaler', 'Kôichi Madanbashi vit dans un univers dominé par “l’empire Pingouin” (Penguin Empire). Seule une organisation secrète, du nom de Prince Beauty Parlor, s’oppose à l’empire avec plus ou moins de succès.
INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('119c2f8c-4594-4bd4-9e22-332340bc7c7e', 'Girls und Panzer: Saishuushou 4', '4eme partie du film Girls und Panzer : Saishuushou', 'https://v6.voiranime.com/wp-content/uploads/2024/11/thumb_6744abbc96eb8-193x278.jpg', '', 4, NULL, '2025-05-01T23:55:44.685Z', '2025-05-01T23:55:44.685Z');
