INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('e339f48e-6a05-4383-8d78-6d21cf595aed', 'Devils’ Line: Anytime Anywhere', 'Épisode spécial inclus avec le tome 12 du manga, qui sort au Japon le 23 août 2018. L’épisode se déroulera après les événements de la série animée et l’histoire suivra la relation entre Tamaki et Midori, les parents d’Anzai.', 'https://v6.voiranime.com/wp-content/uploads/2024/10/thumb_671241e8bd283-193x278.png', '', 4.1, NULL, '2025-05-01T23:56:08.771Z', '2025-05-01T23:56:08.771Z');
INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('12add01e-b7eb-4d7c-8701-578966d7319c', 'Bakuten!! Movie', 'Le tournoi interlycées national approche à grands pas, et les six membres du club de GRS masculine du lycée Ao se préparent à donner le meilleur d’eux-mêmes.', 'https://v6.voiranime.com/wp-content/uploads/2024/10/thumb_67124124efd67-193x278.jpg', '', 3.9, NULL, '2025-05-01T23:56:12.694Z', '2025-05-01T23:56:12.694Z');
INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('803f7fe4-bdf0-4642-a519-f12928ae2274', 'Lonely Castle in the Mirror', 'L’histoire se déroule en partie dans un monde onirique où se trouve un vieux château installé à flanc de rocher, au milieu d’un vaste océan.
INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('46ba42d8-beb9-41e5-b4ee-5c84f2c8f6c3', 'Re:ZERO -Starting Life in Another World- (Re-Edit) (VF)', 'Il s’agit d’une version spéciale compilant la première saison de Re:Zero et contenant des scènes inédites.', 'https://v6.voiranime.com/wp-content/uploads/2020/04/a11z54rr1e4a2s4d2zerras1-193x278.jpg', '', 4.3, NULL, '2025-05-01T23:56:20.568Z', '2025-05-01T23:56:20.568Z');
