INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('2c34c107-e52b-4f8c-91b9-ee273f8df0a4', 'BASTARD!!: Ankoku no Hakaishin 2 (VF)', 'Saison 2 de “BASTARD!!: Ankoku no Hakaishin”', 'https://v6.voiranime.com/wp-content/uploads/2024/08/thumb_66bd266342687-193x278.jpg', '', 2, NULL, '2025-05-01T23:56:52.380Z', '2025-05-01T23:56:52.380Z');
INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('8267f579-e4f0-40a9-accf-ec384c39eca4', 'Ryuu to Sobakasu no Hime (VF)', 'L’histoire d’une adolescente de 17 ans, Suzu, dont le quotidien se partage entre le petit village où elle vit avec son père et “U”, un monde virtuel aux cinq milliards d’abonnés dont elle est l’une des icônes, la chanteuse Belle. Lorsqu’elle rencontre une mystérieuse créature, elle se lance dans une quête pleine d’aventures.', 'https://v6.voiranime.com/wp-content/uploads/2022/01/thumb_61d4aae74eb21-193x278.jpg', '', 4.3, NULL, '2025-05-01T23:56:56.331Z', '2025-05-01T23:56:56.331Z');
INSERT INTO animes (id, title, description, poster, backdrop, rating, year, created_at, updated_at) VALUES ('b9800b69-4506-497b-ac90-afc58a72cf54', 'Legend of Light (VF)', 'L’héroïne de l’histoire, Hikari Uenaga, est une collégienne de quinze ans qui se découvre une passion pour la gymnastique rythmique et sportive en voyant son idole, Diliana Gueorguiva, gagner une compétition en dépit de son jeune âge. Lorsqu’elle rencontre son idole, celle-ci lui propose de l’entraîner en Bulgarie où elle pratique la GRS professionnellement. Hikari accepte et commence rapidement son entraînement. Plusieurs années plus tard, Hikari s’entraîne dur pour devenir la meilleure gymnaste de son école, et devient membre de l’équipe de GRS.', 'https://v6.voiranime.com/wp-content/uploads/2024/07/thumb_66858ab64034a-193x278.png', '', 3.6, NULL, '2025-05-01T23:57:00.266Z', '2025-05-01T23:57:00.266Z');
